import { getEnv } from '../utils.script';
import { Pool } from '@interest-protocol/vortex-sdk';

const VERIFYING_KEY =
  '';

(async () => {
  const { adminSdk, executeTx, pools } = await getEnv();

  const tx = adminSdk.setGroth16Vk({
    pool: pools[Pool.shrimp].objectId,
    vk: VERIFYING_KEY,
  });

  await executeTx(tx);
})();
